jQuery(function($){$('.gridos_loadmore').click(function(){var button=$(this),layoutPost=$(this).attr('data-layout'),postsNumber=$(this).attr('data-post-num'),postsOrder=$(this).attr('data-order'),postsCategory=$(this).attr('data-category'),postsTags=$(this).attr('data-tag'),postsComment=$(this).attr('data-comment'),postsPopular=$(this).attr('data-popular'),postsCustom=$(this).attr('data-custom'),data={'action':'loadmore','query':gridos_loadmore_params.posts,'page':gridos_loadmore_params.current_page,'layoutPost':layoutPost,'postsNumber':postsNumber,'postsOrder':postsOrder,'postsCategory':postsCategory,'postsTags':postsTags,'postsComment':postsComment,'postsPopular':postsPopular,'postsCustom':postsCustom};$.ajax({url:gridos_loadmore_params.ajaxurl,data:data,type:'POST',beforeSend:function(xhr){button.text('Loading...');},success:function(data){if(data){button.text('More posts');var storage=document.createElement('div');var $elems;$(storage).append(data);$elems=$(storage).find('.item').appendTo('.iso-call');$('.recent-box.iso-call').isotope('appended',$elems);gridos_loadmore_params.current_page++;if(data.length<5){button.remove();}}else{button.remove();}}});});var canBeLoaded=false,bottomOffset=2000;if($('.gridos_loadmore').hasClass('infinite-box')){canBeLoaded=true;var layoutPost=$('.infinite-box').attr('data-layout'),postsNumber=$('.infinite-box').attr('data-post-num'),postsOrder=$('.infinite-box').attr('data-order'),postsCategory=$('.infinite-box').attr('data-category'),postsTags=$('.infinite-box').attr('data-tag'),postsComment=$('.infinite-box').attr('data-comment'),postsPopular=$('.infinite-box').attr('data-popular'),postsCustom=$('.infinite-box').attr('data-custom');}
$(window).scroll(function(){var data={'action':'loadmore','query':gridos_loadmore_params.posts,'page':gridos_loadmore_params.current_page,'layoutPost':layoutPost,'postsNumber':postsNumber,'postsOrder':postsOrder,'postsCategory':postsCategory,'postsTags':postsTags,'postsComment':postsComment,'postsPopular':postsPopular,'postsCustom':postsCustom};if($(document).scrollTop()>($(document).height()-bottomOffset)&&canBeLoaded==true){$.ajax({url:gridos_loadmore_params.ajaxurl,data:data,type:'POST',beforeSend:function(xhr){canBeLoaded=false;},success:function(data){if(data){var storage=document.createElement('div');var $elems;$(storage).append(data);$elems=$(storage).find('.item').appendTo('.iso-call');$('.recent-box.iso-call').isotope('appended',$elems);canBeLoaded=true;gridos_loadmore_params.current_page++;}}});}});});